/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `products` (
  `Id` int(11) NOT NULL AUTO_INCREMENT COMMENT '编号',
  `ProductName` varchar(20) NOT NULL COMMENT '商品名称',
  `ProductStyle` varchar(10) NOT NULL COMMENT '款别 取值有：必定款，推广款，普通款',
  `ProductClass` varchar(10) NOT NULL COMMENT '类别 取值有：床，桌，椅等',
  `ProductSerial` varchar(10) NOT NULL COMMENT '系列 取值有：办公，家具，装饰，收藏',
  `ProductCode` varchar(20) NOT NULL COMMENT '货号',
  `color` varchar(10) NOT NULL COMMENT '颜色',
  `size` varchar(10) NOT NULL COMMENT '尺寸',
  `PriceZone` varchar(20) DEFAULT NULL COMMENT '价格带',
  `Designer` varchar(20) DEFAULT NULL COMMENT '设计师',
  `Advantages` varchar(100) DEFAULT NULL COMMENT '卖点',
  `Pictures` varchar(500) NOT NULL COMMENT '所有商品图片的路径，逗号分隔，商品首图 为第一个路径',
  `MatchIds` varchar(50) DEFAULT NULL COMMENT '搭配编号',
  `Deleted` bit(1) DEFAULT b'0' COMMENT '是否删除款',
  `OrderRank` int(11) DEFAULT NULL COMMENT '订货件数排名 只需标注前十名',
  `MinOrder` varchar(100) DEFAULT NULL COMMENT '起订量 买家下单件数少于起订量，不允许提交订单,取值有：-80-:2,80-150:4,150-300:8,300-500:16,500+:32',
  `OrderNumber` int(11) DEFAULT '0' COMMENT '已订件数',
  `Price` varchar(4000) NOT NULL COMMENT '价格',
  `StyleCode` varchar(20) DEFAULT NULL COMMENT '款号',
  `IsShowing` bit(1) DEFAULT b'0',
  `OrderAmount` int(11) DEFAULT '0',
  PRIMARY KEY (`Id`),
  KEY `IDX_products_Style` (`ProductStyle`),
  KEY `IDX_products_Class` (`ProductClass`),
  KEY `IDX_products_Serial` (`ProductSerial`),
  KEY `IDX_products_PriceZone` (`PriceZone`),
  KEY `IDX_products_ProductCode` (`ProductCode`)
) ENGINE=InnoDB AUTO_INCREMENT=113 DEFAULT CHARSET=utf8 COMMENT='商品表 商品详细信息';
